      customProps: {
        textAlign: 'right',
      },
    },
  ], []);

  const data = React.useMemo(() => [
    { id: 1, eventType: 'Virus/Malware', affectedDevices: 20, detections: 634 },
    { id: 2, eventType: 'Spyware/Grayware', affectedDevices: 20, detections: 634 },
    { id: 3, eventType: 'URL Filtering', affectedDevices: 15, detections: 598 },
    { id: 4, eventType: 'Web Reputation', affectedDevices: 15, detections: 598 },
    { id: 5, eventType: 'Network Virus', affectedDevices: 15, detections: 497 },
    { id: 6, eventType: 'Application Control', affectedDevices: 0, detections: 0 }
  ], []);

const { colorMode } = useColorMode();
  const { colors } = useTheme();
  const columnPlaceholderProps = {
    'dark': {
      backgroundColor: 'gray:90',
      color: 'gray:50',
    },
    'light': {
      backgroundColor: 'gray:10',
      color: 'gray:30',
    }
  }[colorMode];
  const columnDraggerProps = {
    'dark': {
      backgroundColor: 'gray:80',
      color: 'gray:30',
      boxShadow: '0 4px 16px 0 rgba(0, 0, 0, 0.48), 0 2px 4px 0 rgba(0, 0, 0, 0.16)',
      border: `1px solid ${colors['gray:60']}`,
    },
    'light': {
      backgroundColor: 'gray:10',
      color: 'gray:80',
      boxShadow: '0 4px 16px 0 rgba(0, 0, 0, 0.16), 0 2px 4px 0 rgba(0, 0, 0, 0.08)',
      border: `1px solid ${colors['gray:20']}`,
    }
  }[colorMode];

const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
    allColumns,
    setColumnOrder,
  } = useTable(
    {
      columns,
      data,
      defaultColumn: {},
    },
    useBlockLayout,
    useColumnOrder,
  );

  const currentColOrder = useRef();
  const [placeholderProps, setPlaceholderProps] = useState({});
  const onDragEnd = (result) => {
    setPlaceholderProps({});

    // dropped outside the list
    if (!result.destination) {
      return;
    }
  };
  const onDragUpdate = (dragUpdateObj, b) => {
    if(!dragUpdateObj.destination){
      return;
    }
    const draggableId = dragUpdateObj.draggableId;
    const destinationIndex = dragUpdateObj.destination.index;

    const queryAttr = "data-rbd-drag-handle-draggable-id";
    const domQuery = `[${queryAttr}='${draggableId}']`;
    const draggedDOM = document.querySelector(domQuery);

const queryAttr = "data-rbd-drag-handle-draggable-id";
    const domQuery = `[${queryAttr}='${draggableId}']`;
    const draggedDOM = document.querySelector(domQuery);

    if (!draggedDOM) {
      return;
    }
    const { clientHeight, offsetWidth } = draggedDOM;
    const clientX = Array.from(draggedDOM.parentNode.children)
      .slice(0, destinationIndex)
      .reduce((total, curr) => {
        return total + curr.clientWidth;
      }, 0);

    setPlaceholderProps({
      clientHeight,
      clientWidth: offsetWidth,
      clientY: 2,
      clientX: clientX,
      content: draggedDOM.innerHTML,
    });

setPlaceholderProps({
      clientHeight,
      clientWidth: offsetWidth,
      clientY: 2,
      clientX: clientX,
      content: draggedDOM.innerHTML,
    });

    const colOrder = [...currentColOrder.current];
    const sIndex = dragUpdateObj.source.index;
    const dIndex = dragUpdateObj.destination && dragUpdateObj.destination.index;
    if (typeof sIndex === "number" && typeof dIndex === "number") {
      colOrder.splice(sIndex, 1);
      colOrder.splice(dIndex, 0, dragUpdateObj.draggableId);
      setColumnOrder(colOrder);
    }
  };
  const onDragStart = () => {
    currentColOrder.current = allColumns.map(o => o.id);
  };

return (
    <Table {...getTableProps()}>
      <TableHeader>
        {headerGroups.map((headerGroup, index) => (
          <DragDropContext
            key={index}
            onDragStart={onDragStart}
            onDragUpdate={onDragUpdate}
            onDragEnd={onDragEnd}
          >
            <Droppable
              droppableId="droppable-table-header"
              direction="horizontal"
            >
              {(droppableProvided, droppableSnapshot) => {
                return (
                  <TableHeaderRow
                    ref={droppableProvided.innerRef}
                    {...droppableProvided.droppableProps}
                    {...headerGroup.getHeaderGroupProps()}
                  >
                    {headerGroup.headers.map((column, index) => (
                      <Draggable
                        key={column.id}
                        draggableId={column.id}
                        index={index}

{...columnHeaderProps}
                              {...column.customProps}
                              {...snapshot.isDragging && columnDraggerProps}
                              ref={provided.innerRef}
                              userSelect="none"
                              style={columnHeaderStyle}
                            >
                              {column.render("Header")}
                            </TableHeaderCell>
                          );
                        }}
                      </Draggable>
                    ))}
                    {droppableProvided.placeholder}
                    <TableHeaderCell
                      position="absolute"
                      top={placeholderProps.clientY}
                      left={placeholderProps.clientX}
                      height={placeholderProps.clientHeight}
                      width={placeholderProps.clientWidth}

left={placeholderProps.clientX}
                      height={placeholderProps.clientHeight}
                      width={placeholderProps.clientWidth}
                      display={placeholderProps.clientWidth ? 'block' : 'none' }
                      {...columnPlaceholderProps}
                    >
                      { placeholderProps.content }
                    </TableHeaderCell>
                  </TableHeaderRow>
                );
              }}
            </Droppable>
          </DragDropContext>
        ))}
      </TableHeader>
      <TableBody {...getTableBodyProps()}>
        {rows.map((row, i) => {
          prepareRow(row);
          return (
            <TableRow key={row.id} {...row.getRowProps()}>
              {
                row.cells.map(cell => {
                  return (
                    <TableCell
                      key={cell.column.id}
                      {...cell.getCellProps()}

row.cells.map(cell => {
                  return (
                    <TableCell
                      key={cell.column.id}
                      {...cell.getCellProps()}
                      {...cell.column.customProps}
                    >
                      {cell.render('Cell')}
                    </TableCell>
                  );
                })
              }
            </TableRow>
          );
        })}
      </TableBody>
    </Table>
  );
};

import {
  Box,
  Table,
  TableHeader,
  TableHeaderRow,
  TableHeaderCell,
  TableBody,
  TableRow,
  TableCell,
  useColorMode,
} from '@tonic-ui/react';
import React, { useCallback, useEffect, useRef, useState } from 'react';
import { DndProvider, useDrop, useDrag, useDragLayer } from 'react-dnd';
import { HTML5Backend, getEmptyImage } from 'react-dnd-html5-backend';
import immutableUpdate from 'immutability-helper';

const ItemTypes = {
  TR: 'tr',
};

const TR = ({ id, row, index, moveTr, ...otherProps }) => {
  const ref = useRef(null);
  const [, drop] = useDrop({
    accept: ItemTypes.TR,
    hover(item, monitor) {
      if (!ref.current) {
        return;
      }
      const dragIndex = item.index;
      const hoverIndex = index;
      // Don't replace items with themselves
      if (dragIndex === hoverIndex) {
        return;
      }
      // Determine rectangle on screen
      const hoverBoundingRect = ref.current ? ref.current.getBoundingClientRect() : {};
      // Get vertical middle
      const hoverMiddleY = (hoverBoundingRect.bottom - hoverBoundingRect.top) / 2;
      // Determine mouse position
      const clientOffset = monitor.getClientOffset();
      // Get pixels to the top
      const hoverClientY = clientOffset.y - hoverBoundingRect.top;
      // Only perform the move when the mouse has crossed half of the items height
      // When dragging downwards, only move when the cursor is below 50%

// Only perform the move when the mouse has crossed half of the items height
      // When dragging downwards, only move when the cursor is below 50%
      // When dragging upwards, only move when the cursor is above 50%
      // Dragging downwards
      if (dragIndex < hoverIndex && hoverClientY < hoverMiddleY) {
        return;
      }
      // Dragging upwards
      if (dragIndex > hoverIndex && hoverClientY > hoverMiddleY) {
        return;
      }
      // Time to actually perform the action
      moveTr(dragIndex, hoverIndex);
      // Note: we're mutating the monitor item here!
      // Generally it's better to avoid mutations,
      // but it's good here for the sake of performance
      // to avoid expensive index searches.
      item.index = hoverIndex;
    },
  });
  const [{ isDragging }, drag, preview] = useDrag({
    type: ItemTypes.TR,
    item: { id, index, row },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  });

drag(drop(ref));

  useEffect(() => {
    preview(getEmptyImage(), { captureDraggingState: true });
  }, [preview]);

  return (
    <TableRow
      {...otherProps}
      ref={ref}
      style={{
        cursor: 'move',
        opacity: isDragging ? 0 : 1,
      }}
    >
      <TableCell width="240px">{row.eventType}</TableCell>
      <TableCell width="140px" textAlign="right">{row.affectedDevices}</TableCell>
      <TableCell width="136px" textAlign="right">{row.detections}</TableCell>
    </TableRow>
  );
};

const CustomDragLayer = (props) => {
  const { itemType, isDragging, item, initialOffset, currentOffset, } = useDragLayer((monitor) => ({
    item: monitor.getItem(),
    itemType: monitor.getItemType(),
    initialOffset: monitor.getInitialSourceClientOffset(),
    currentOffset: monitor.getSourceClientOffset(),
    isDragging: monitor.isDragging(),
  }));

  const layerStyles = {
    position: 'fixed',
    pointerEvents: 'none',
    zIndex: 100,
    left: 0,
    top: 0,
  };

  const getItemStyles = (initialOffset, currentOffset) => {
    if (!initialOffset || !currentOffset) {
      return {
        display: 'none',
      };
    }
    const { x, y } = currentOffset;
    const transform = `translate(${x}px, ${y}px)`;
    return {
      transform,
      WebkitTransform: transform,
    };
  };

  if (!isDragging) {
    return null;
  }

if (!isDragging) {
    return null;
  }

  if (itemType === ItemTypes.TR) {
    const row = item.row;
     return (
       <Box style={layerStyles}>
        <Box style={getItemStyles(initialOffset, currentOffset)}>
          <TableRow {...props}>
            <TableCell width="240px">{row.eventType}</TableCell>
            <TableCell width="140px" textAlign="right">{row.affectedDevices}</TableCell>
            <TableCell width="136px" textAlign="right">{row.detections}</TableCell>
          </TableRow>
        </Box>
      </Box>
    );
  }

  return null;
};

const App = () => {
  const [items, setItems] = useState([
    { id: 1, eventType: 'Virus/Malware', affectedDevices: 20, detections: 634 },
    { id: 2, eventType: 'Spyware/Grayware', affectedDevices: 20, detections: 634 },
    { id: 3, eventType: 'URL Filtering', affectedDevices: 15, detections: 598 },
    { id: 4, eventType: 'Web Reputation', affectedDevices: 15, detections: 598 },
    { id: 5, eventType: 'Network Virus', affectedDevices: 15, detections: 497 },
    { id: 6, eventType: 'Application Control', affectedDevices: 0, detections: 0 }
  ]);
  const moveTr = useCallback((dragIndex, hoverIndex) => {
    const dragCard = items[dragIndex];
    setItems(immutableUpdate(items, {
      $splice: [
        [dragIndex, 1],
        [hoverIndex, 0, dragCard],
      ],
    }));
  }, [items]);
  const { colorMode } = useColorMode();
  const tableProps = {
    'dark': {
      backgroundColor: 'gray:80',
    },
    'light': {
      backgroundColor: 'gray:10',
    }
  }[colorMode];

const { colorMode } = useColorMode();
  const tableProps = {
    'dark': {
      backgroundColor: 'gray:80',
    },
    'light': {
      backgroundColor: 'gray:10',
    }
  }[colorMode];
  const rowProps = {
    'dark': {
      backgroundColor: 'gray:100',
    },
    'light': {
      backgroundColor: 'white',
    },
  }[colorMode];

return (
    <DndProvider backend={HTML5Backend}>
      <Table
       {...tableProps}
      >
        <TableHeader>
          <TableHeaderRow
             {...rowProps}
          >
            <TableHeaderCell width="240px">Event Type</TableHeaderCell>
            <TableHeaderCell width="140px" textAlign="right">Affected Devices</TableHeaderCell>
            <TableHeaderCell width="136px" textAlign="right">Detections</TableHeaderCell>
          </TableHeaderRow>
        </TableHeader>
        <TableBody>
          {
            items.map((item, i) => (
              <TR {...rowProps} key={item.id} index={i} id={item.id} row={item} moveTr={moveTr}/>
            ))
          }
          <CustomDragLayer {...rowProps} />
        </TableBody>
      </Table>
    </DndProvider>
  );
};

export default App;

