            rowGap="1x"
          >
            {entries.map(([key, value]) => {
              return (
                <Fragment key={key}>
                  <TextLabel>
                    {key}
                  </TextLabel>
                  <Text>
                    {renderValue(value)}
                  </Text>
                </Fragment>
              );
            })}
          </Grid>
        </Box>
      </Flex>
    );
  };

  /*
  const renderExpandedRow = ({ row }) => {
    const tableBorderColor = {
      dark: 'gray:70',
      light: 'gray:30',
    }[colorMode];
    const dividerColor = {
      dark: 'gray:60',
      light: 'gray:30',
    }[colorMode];

    return (
      <Flex
        borderBottom={1}
