};

export default App;

```

### Access the state of tabs

Sometimes you need to access the state of tabs. For example, you may want to lazily load the content of a tab when that tab is selected.

`Tabs`, `Tab`, and `TabPanel` components expose internal state using **Function as Children (FaCC)**. This is not a recommended approach, but it is useful for this use case.

```js
import {
  Box,
  ButtonBase,
  Tab,
  TabList,
  TabPanel,
  TabPanels,
  Tabs,
} from '@tonic-ui/react';
import React from 'react';

const App = () => {
  return (
    <Tabs defaultIndex={1}>
      {({ index: selectedIndex }) => (
        <>
          <Box mb="2x">
            TAB {selectedIndex} is selected
          </Box>
          <TabList>
            {[1, 2, 3].map(tabIndex => (
              <Tab key={tabIndex} index={tabIndex}>
                {({ getTabProps, disabled, index, isSelected }) => {
                  return (
                    <ButtonBase {...getTabProps()}>
                      TAB {index}
                    </ButtonBase>
                  );
                }}
              </Tab>
            ))}
          </TabList>
          <TabPanels px="3x" py="2x">
            {[1, 2, 3].map(tabPanelIndex => (
              <TabPanel key={tabPanelIndex} index={tabPanelIndex}>
                {({ getTabPanelProps, index, variant }) => {
                  return (
                    <Box {...getTabPanelProps()}>
                      Tab Panel {index}
                    </Box>
                  );
                }}
              </TabPanel>
            ))}
          </TabPanels>
        </>
      )}
    </Tabs>
  );
};

export default App;

```

### Tabs with dropdown menus

The tab with the dropdown menu is usually the last tab. You can use `event.preventDefault()` to control whether the tab will be selected when the user clicks the dropdown menu.

```js
import {
  Box,
  Menu,
  MenuList,
  MenuItem,
  MenuToggle,
  MenuToggleIcon,
  Tab,
  TabList,
  Tabs,
  Text,
} from '@tonic-ui/react';
import React from 'react';

const App = () => {
  return (
    <Tabs variant="default">
      <TabList>
        <Tab>TAB 1</Tab>
        <Tab>TAB 2</Tab>
        <Tab
          onClick={(event) => {
            const value = event.target.getAttribute('value');
            if (!value) {
              event.preventDefault();
              return;
            }
            console.log('Menu Item ' + value + ' clicked');
          }}
        >
          <Menu offset={[-14, 10]}>
            <MenuToggle
              as={Box}
              alignItems="center"
              columnGap="1x"
            >
              <Text>TAB 3</Text>
              <MenuToggleIcon />
            </MenuToggle>
            <MenuList
              width="max-content"
            >
              <MenuItem value={1}>Menu Item 1</MenuItem>
              <MenuItem value={2}>Menu Item 2</MenuItem>
              <MenuItem value={3}>Menu Item 3</MenuItem>
            </MenuList>
          </Menu>
        </Tab>
      </TabList>
    </Tabs>
  );
};

export default App;

```

### Draggable tabs with dropdown menus

Use [react-beautiful-dnd](https://github.com/atlassian/react-beautiful-dnd) to make tabs draggable.

```js
import {
  Box,
  Menu,
  MenuList,
  MenuItem,
  MenuToggle,
  MenuToggleIcon,
  Tab,
  TabList,
  TabPanel,
  TabPanels,
  Tabs,
  Text,
} from '@tonic-ui/react';
import * as rbd from 'react-beautiful-dnd';
import React, { useState } from 'react';

// a little function to help us with reordering the result
const reorder = (list, startIndex, endIndex) => {
  const result = Array.from(list);
  const [removed] = result.splice(startIndex, 1);
  result.splice(endIndex, 0, removed);
  return result;
};

const App = () => {
  const [items, setItems] = useState([
    {
      id: 'tab-1',
      label: 'TAB 1',
      content: 'TAB 1',
    },
    {
      id: 'tab-2',
      label: 'TAB 2',
      content: 'TAB 2',
    },
    {
      id: 'tab-3',
      label: 'TAB 3',
      content: 'TAB 3',
      dropdown: {
        items: [
          { id: 'menuitem-1', label: 'Menu Item 1' },
          { id: 'menuitem-2', label: 'Menu Item 2' },
          { id: 'menuitem-3', label: 'Menu Item 3' },
        ],
      },
    },
  ]);
  const onDragEnd = (result) => {
    // dropped outside the list
    if (!result.destination) {
      return;
    }

    const nextItems = reorder(items, result.source.index, result.destination.index);
    setItems(nextItems);
  };

  return (
    <Tabs
      orientation="horizontal"
    >
      <rbd.DragDropContext
        onDragEnd={onDragEnd}
      >
        <rbd.Droppable
          droppableId="droppable"
          direction="horizontal"
        >
          {(droppableProvided, droppableSnapshot) => (
            <TabList
              ref={droppableProvided.innerRef}
              {...droppableProvided.droppableProps}
            >
              {items.map((item, index) => (
                <rbd.Draggable
                  key={item.id}
                  draggableId={item.id}
                  index={index}
                >
                  {(draggableProvided, draggableSnapshot) => (
                    <Box
                      ref={draggableProvided.innerRef}
                      {...draggableProvided.draggableProps}
                      {...draggableProvided.dragHandleProps}
                      style={{
                        ...draggableProvided.draggableProps.style,
                      }}
                    >
                      {!item.dropdown && (
                        <Tab as={Box} index={index}>
                          {item.label}
                        </Tab>
                      )}
                      {item.dropdown && (
                        <Tab
                          as={Box}
                          onClick={(event) => {
                            const value = event.target.getAttribute('value');
                            if (!value) {
                              event.preventDefault();
                              return;
                            }
                            console.log('Menu Item ' + value + ' clicked');
                          }}
                        >
                          <Menu offset={[-14, 10]}>
                            <MenuToggle
                              as={Box}
                              alignItems="center"
                              columnGap="1x"
                            >
                              <Text>{item.label}</Text>
                              <MenuToggleIcon />
                            </MenuToggle>
                            <MenuList
                              width="max-content"
                            >
                              {item.dropdown.items.map(x => (
                                <MenuItem key={x.id} value={x.id}>
                                  {x.label}
                                </MenuItem>
                              ))}
                            </MenuList>
                          </Menu>
                        </Tab>
                      )}
                    </Box>
                  )}
                </rbd.Draggable>
              ))}
              {droppableProvided.placeholder}
            </TabList>
          )}
        </rbd.Droppable>
      </rbd.DragDropContext>
      <TabPanels px="3x" py="2x">
        {items.map((item, index) => (
          <TabPanel
            key={item.id}
            index={index}
          >
            {item.content}
          </TabPanel>
        ))}
      </TabPanels>
    </Tabs>
  );
};

export default App;

```

### Scrollable tabs

Scrollable tabs are useful when the tab list is longer than the tab container. You can use the following code to create a scrollable tabs.

> This example teaches you how to create a scrollable tabs that supports drag-to-scroll behavior. Scrolling tabs is currently not part of this component library.

```js
import {
  Box,
  ButtonBase,
  Flex,
  Icon,
  Tab,
  TabList,
  TabPanel,
  TabPanels,
  Tabs,
  useColorMode,
  useColorStyle,
} from '@tonic-ui/react';
import {
  useEffectOnce,
} from '@tonic-ui/react-hooks';
import React, { forwardRef, useCallback, useEffect, useMemo, useRef, useState } from 'react';

const easeInOutSin = (time) => {
  return (1 + Math.sin(Math.PI * time - Math.PI / 2)) / 2;
};

const animate = (property, element, to, options = {}, cb = () => {}) => {
  const {
    ease = easeInOutSin,
    duration = 300, // standard
  } = options;

  let start = null;
  const from = element[property];
  let cancelled = false;

  const cancel = () => {
    cancelled = true;
  };

  const step = (timestamp) => {
    if (cancelled) {
      cb(new Error('Animation cancelled'));
      return;
    }

    if (start === null) {
      start = timestamp;
    }
    const time = Math.min(1, (timestamp - start) / duration);

    element[property] = ease(time) * (to - from) + from;

    if (time >= 1) {
      requestAnimationFrame(() => {
        cb(null);
      });
      return;
    }

    requestAnimationFrame(step);
  };

  if (from === to) {
    cb(new Error('Element already at target position'));
    return cancel;
  }

  requestAnimationFrame(step);
  return cancel;
};

const debounce = (func, wait = 166) => {
  let timeout;
  function debounced(...args) {
    const later = () => {
      func.apply(this, args);
    };
    clearTimeout(timeout);
    timeout = setTimeout(later, wait);
  }

  debounced.clear = () => {
    clearTimeout(timeout);
  };

  return debounced;
}

const IconButton = (props) => {
  const [colorMode] = useColorMode();
  const [colorStyle] = useColorStyle({ colorMode });
  return (
    <ButtonBase
      px="2x"
      color={colorStyle.color.secondary}
      tabIndex={0}
      _disabled={{
        color: colorStyle.color.disabled,
      }}
      _hover={{
        color: colorStyle.color.primary,
      }}
      {...props}
    />
  );
};

const StartScrollIndicator = forwardRef((props, ref) => (
  <Box
    ref={ref}
    position="relative"
    __after={{
      content: '""',
      position: 'absolute',
      top: 0,
      bottom: 0,
      left: 0,
      width: '4x',
      background: 'linear-gradient(to right, rgba(21, 21, 21, 1) 0%, rgba(21, 21, 21, 0) 100%)',
    }}
    {...props}
  />
));
StartScrollIndicator.displayName = 'StartScrollIndicator';

const EndScrollIndicator = forwardRef((props, ref) => (
  <Box
    ref={ref}
    position="relative"
    __after={{
      content: '""',
      position: 'absolute',
      top: 0,
      bottom: 0,
      right: 0,
      width: '4x',
      background: 'linear-gradient(to left, rgba(21, 21, 21, 1) 0%, rgba(21, 21, 21, 0) 100%)',
    }}
    {...props}
  />
));
EndScrollIndicator.displayName = 'EndScrollIndicator';

const useDragToScroll = (ref, options) => {
  const {
    direction = 'both', // One of: 'vertical', 'horizontal', 'both'
  } = { ...options };

  useEffect(() => {
    const position = {
      scrollTop: 0,
      scrollLeft: 0,
      mouseX: 0,
      mouseY: 0,
    };

    const el = ref && ref.current;
    if (!el) {
      return;
    }

    const mouseMoveHandler = (event) => {
      const dx = event.clientX - position.mouseX;
      const dy = event.clientY - position.mouseY;
      if (direction !== 'horizontal') {
        el.scrollTop = position.scrollTop - dy;
      }
      if (direction !== 'vertical') {
        el.scrollLeft = position.scrollLeft - dx;
      }
    };

    const mouseUpHandler = () => {
      el.style.cursor = 'grab';
      el.style.removeProperty('user-select');
      document.removeEventListener('mousemove', mouseMoveHandler);
      document.removeEventListener('mouseup', mouseUpHandler);
    };

    const onMouseDown = (event) => {
      position.scrollLeft = el.scrollLeft;
      position.scrollTop = el.scrollTop;
      position.mouseX = event.clientX;
      position.mouseY = event.clientY;
      el.style.cursor = 'grabbing';
      el.style.userSelect = 'none';
      document.addEventListener('mousemove', mouseMoveHandler);
      document.addEventListener('mouseup', mouseUpHandler);
    };

    el.addEventListener('mousedown', onMouseDown);
    return () => {
      el.removeEventListener('mousedown', onMouseDown);
    }
  }, [direction, ref]);
};

const App = () => {
  const startScrollIndicatorRef = useRef();
  const endScrollIndicatorRef = useRef();
  const orientation = 'horizontal';
  const scrollStart = (orientation === 'vertical') ? 'scrollTop' : 'scrollLeft';
  const clientSize = (orientation === 'vertical') ? 'clientHeight' : 'clientWidth';
  const [displayScroll, setDisplayScroll] = useState({
    start: false,
    end: false,
  });
  const tabScrollerRef = useRef(null);
  const tabListRef = useRef(null);

  useDragToScroll(tabScrollerRef);

  const scroll = (scrollValue, { animation = true } = {}) => {
    if (animation) {
      animate(scrollStart, tabScrollerRef.current, scrollValue, {
        duration: 300,
      });
    } else {
      tabScrollerRef.current[scrollStart] = scrollValue;
    }
  };

  const moveTabsScroll = (delta) => {
    let scrollValue = tabScrollerRef.current[scrollStart];
    scrollValue += delta;
    scroll(scrollValue);
  };

  // TODO: use Intersection Observer API to get expected scroll position
  const getScrollSize = () => {
    const containerSize = tabScrollerRef.current[clientSize];
    let totalSize = 0;
    const children = Array.from(tabListRef.current.children);
    for (let i = 0; i < children.length; i += 1) {
      const tab = children[i];
      if (totalSize + tab[clientSize] > containerSize) {
        break;
      }
      totalSize += tab[clientSize];
    }
    return totalSize;
  };

  const handleStartScrollClick = () => {
    moveTabsScroll(-1 * getScrollSize());
  };

  const handleEndScrollClick = () => {
    moveTabsScroll(getScrollSize());
  };

  const updateScrollButtonState = useCallback(() => {
    let showStartScroll;
    let showEndScroll;

    if (orientation === 'vertical') {
      const { scrollTop, scrollHeight, clientHeight } = tabScrollerRef.current;
      showStartScroll = scrollTop > 1;
      showEndScroll = scrollTop < scrollHeight - clientHeight - 1;
    } else {
      const { scrollLeft, scrollWidth, clientWidth } = tabScrollerRef.current;
      // use 1 for the potential rounding error with browser zooms.
      showStartScroll = scrollLeft > 1;
      showEndScroll = scrollLeft < scrollWidth - clientWidth - 1;
    }

    if ((showStartScroll !== displayScroll.start) || (showEndScroll !== displayScroll.end)) {
      setDisplayScroll({
        start: showStartScroll,
        end: showEndScroll,
      });
    }
  }, [orientation, displayScroll]);

  const updateScrollIndicatorOpacity = useCallback(() => {
    const { scrollLeft, scrollWidth, clientWidth } = tabScrollerRef.current;
    const startScrollOpacity = 1 / 20 * Math.min(scrollLeft, 20);
    const endScrollOpacity = 1 / 20 * ((scrollWidth - clientWidth) - Math.max(scrollLeft, (scrollWidth - clientWidth - 20)));
    if (startScrollIndicatorRef.current) {
      startScrollIndicatorRef.current.style.opacity = startScrollOpacity;
    }
    if (endScrollIndicatorRef.current) {
      endScrollIndicatorRef.current.style.opacity = endScrollOpacity;
    }
  }, []);

  const handleScrollTabs = useMemo(() => {
    return debounce(() => {
      updateScrollButtonState();
      updateScrollIndicatorOpacity();
    }, Math.floor(1000 / 60));
  }, [updateScrollButtonState, updateScrollIndicatorOpacity]);

  useEffect(() => {
    return () => {
      handleScrollTabs.clear();
    };
  }, [handleScrollTabs]);

  useEffectOnce(() => {
    updateScrollButtonState();
    updateScrollIndicatorOpacity();
  });

  return (
    <Tabs>
      <Flex>
        <IconButton
          disabled={!displayScroll.start}
          onClick={handleStartScrollClick}
        >
          <Icon icon="angle-left" />
        </IconButton>
        <StartScrollIndicator ref={startScrollIndicatorRef} />
        <Box
          ref={tabScrollerRef}
          maxWidth={280}
          overflowX="hidden"
          whiteSpace="nowrap"
          onScroll={handleScrollTabs}
        >
          <TabList
            ref={tabListRef}
          >
            <Tab>TAB 1</Tab>
            <Tab>TAB 2</Tab>
            <Tab>TAB 3</Tab>
            <Tab>TAB 4</Tab>
            <Tab>TAB 5</Tab>
            <Tab>TAB 6</Tab>
            <Tab>TAB 7</Tab>
            <Tab>TAB 8</Tab>
            <Tab>TAB 9</Tab>
            <Tab>TAB 10</Tab>
          </TabList>
        </Box>
        <EndScrollIndicator ref={endScrollIndicatorRef} />
        <IconButton
          disabled={!displayScroll.end}
          onClick={handleEndScrollClick}
        >
          <Icon icon="angle-right" />
        </IconButton>
      </Flex>
      <TabPanels px="3x" py="2x">
        <TabPanel>
          Tab Panel 1
        </TabPanel>
        <TabPanel>
          Tab Panel 2
        </TabPanel>
        <TabPanel>
          Tab Panel 3
        </TabPanel>
        <TabPanel>
          Tab Panel 4
        </TabPanel>
        <TabPanel>
          Tab Panel 5
        </TabPanel>
        <TabPanel>
          Tab Panel 6
        </TabPanel>
        <TabPanel>
          Tab Panel 7
        </TabPanel>
        <TabPanel>
          Tab Panel 8
        </TabPanel>
        <TabPanel>
          Tab Panel 9
        </TabPanel>
        <TabPanel>
          Tab Panel 10
        </TabPanel>
      </TabPanels>
    </Tabs>
  );
};

export default App;

```

### Customization

Tabs also come with an `unstyled` variant. It's useful for doing customizations.

```js
import {
  Box,
  Button,
  ButtonGroup,
  Divider,
  Flex,
  Icon,
  Space,
  Tab,
  TabList,
  TabPanel,
  TabPanels,
  Tabs,
  Text,
  TextLabel,
  useColorMode,
  useColorStyle,
  useTabs,
} from '@tonic-ui/react';
import React, { useState } from 'react';

const CustomTabList = (props) => {
  const context = useTabs();
  const orientation = context.orientation;
  const [colorMode] = useColorMode();
  const [colorStyle] = useColorStyle({ colorMode });
  let styleProps = {
    backgroundColor: colorStyle.background.primary,
    position: 'relative',
  };

  if (orientation === 'horizontal') {
    styleProps = {
      ...styleProps,
      __after: {
        content: '""',
        borderBottom: 1,
        borderBottomColor: colorStyle.divider,
        position: 'absolute',
        bottom: 0,
        width: '100%',
      },
    };
  }

  if (orientation === 'vertical') {
    styleProps = {
      ...styleProps,
      alignItems: 'flex-start',
    };
  }

  return (
    <TabList
      {...styleProps}
      {...props}
    />
  );
};

const CustomTab = ({ children, ...rest }) => {
  const context = useTabs();
  const orientation = context.orientation;
  const [colorMode] = useColorMode();
  const [colorStyle] = useColorStyle({ colorMode });
  const baseStyle = {
    backgroundColor: colorStyle.background.primary,
    color: colorStyle.color.secondary,
    px: '4x',
    py: '3x',
    position: 'relative',
    zIndex: 1,
    _disabled: {
      color: colorStyle.color.disabled,
      cursor: 'not-allowed',
    },
    _hover: {
      color: colorStyle.color.primary,
    },
  };

  if (orientation === 'horizontal') {
    const styleProps = {
      ...baseStyle,
      border: 1,
      borderColor: colorStyle.divider,
      _notLastOfType: {
        borderRight: 'none',
      },
      _selected: {
        borderBottomColor: 'transparent',
        background: colorStyle.background.primary,
        color: colorStyle.color.primary,
        '::before': {
          content: '""',
          position: 'absolute',
          top: -1,
          left: -1,
          right: 0,
          height: 3,
          backgroundColor: '#2cc185',
        },
      },
    };

    return (
      <Tab
        {...styleProps}
        {...rest}
      >
        {children}
      </Tab>
    );
  }

  if (orientation === 'vertical') {
    const styleProps = {
      ...baseStyle,
      borderBottom: 1,
      borderBottomColor: colorStyle.divider,
      textAlign: 'left',
      width: '100%',
      _selected: {
        borderLeftColor: colorStyle.background.primary,
        background: colorStyle.background.primary,
        color: colorStyle.color.primary,
        '::before': {
          content: '""',
          position: 'absolute',
          top: 0,
          bottom: 0,
          left: 0,
          width: 3,
          backgroundColor: '#2cc185',
        },
      },
    };

    return (
      <Tab
        {...styleProps}
        {...rest}
      >
        {({ getTabProps, index, isSelected }) => (
          <Box
            {...getTabProps()}
            pr="1x"
          >
            <Flex justifyContent="space-between">
              <Box>{children}</Box>
              <Icon icon="chevron-right" ml="4x" visibility={isSelected ? 'visible' : 'hidden'} />
            </Flex>
          </Box>
        )}
      </Tab>
    );
  }

  return null;
};

const App = () => {
  const [orientation, setOrientation] = useState('horizontal');
  const [colorMode] = useColorMode();
  const [colorStyle] = useColorStyle({ colorMode });

  return (
    <>
      <Box mb="2x">
        <TextLabel>
          orientation
        </TextLabel>
      </Box>
      <ButtonGroup>
        <Button
          onClick={() => setOrientation('horizontal')}
          variant={orientation === 'horizontal' ? 'primary' : 'secondary'}
        >
          Horizontal
        </Button>
        <Button
          onClick={() => setOrientation('vertical')}
          variant={orientation === 'vertical' ? 'primary' : 'secondary'}
        >
          Vertical
        </Button>
      </ButtonGroup>
      <Divider my="4x" />
      <Tabs
        orientation={orientation}
        variant="unstyled"
      >
        <CustomTabList>
          <CustomTab>
            <Icon icon="home" />
            <Space width="2x" />
            HOME
          </CustomTab>
          <CustomTab>
            <Icon icon="workspace" />
            <Space width="2x" />
            WORKSPACE
          </CustomTab>
          <CustomTab>
            <Icon icon="settings" />
            <Space width="2x" />
            SETTINGS
          </CustomTab>
        </CustomTabList>
        <TabPanels px="4x" py="3x">
          <TabPanel>
            <Text color={colorStyle.color.secondary} fontSize="4rem" lineHeight="1">
                1
            </Text>
          </TabPanel>
          <TabPanel>
            <Text color={colorStyle.color.secondary} fontSize="4rem" lineHeight="1">
              2
            </Text>
          </TabPanel>
          <TabPanel>
            <Text color={colorStyle.color.secondary} fontSize="4rem" lineHeight="1">
              3
            </Text>
          </TabPanel>
        </TabPanels>
      </Tabs>
    </>
  );
};

export default App;

